import React, { useEffect, useState } from 'react';
import Loader from './components/Loader';
import {
  BrowserRouter,
  Routes,
  Route,
} from 'react-router-dom';
import Home from './components/Home';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import app from './config/firebase-config';
import Signin from './components/Signin';
import General from './components/General.js';
import Academic from './components/Academic';
import AcademicReadingIns from './components/AcademicReadingIns';
// Import other components as needed

function App() {
  const [auth, setAuth] = useState(false || window.localStorage.getItem('auth') === 'true');
  const [token, setToken] = useState('');
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (userCred) => {
      if (userCred) {
        setAuth(true);
        window.localStorage.setItem('auth', 'true');
        userCred.getIdToken().then((token) => {
          setToken(token);
          setLoading(false); // Set loading to false when data is ready
        });
      } else {
        setAuth(false);
        setLoading(false); // Set loading to false when data is ready
      }
    });

    return () => unsubscribe();
  }, []);

  return (
    <div>
      {loading ? ( // Display the loader while loading is true
        <Loader />
      ) : (
        <BrowserRouter>
          <Routes>
            <Route path="/" element={auth ? <Home token={token} /> : <Signin setAuth={setAuth} setToken={setToken} />} />
            <Route path="/general" element={<General />} />
            <Route path="/academic" element={<Academic />} />
            <Route path="/academicreadingins" element={<AcademicReadingIns />} />
            {/* Add other routes here */}
          </Routes>
        </BrowserRouter>
      )}
    </div>
  );
}

export default App;
