import React, { useState } from 'react';
import './GeneralWritingTask1.css';
import axios from 'axios'; 
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

const GeneralWritingTask1 = () => {
    const [userAnswer, setUserAnswer] = useState(''); 
    const [feedback, setFeedback] = useState('');
 
    const handleUserAnswerChange = (event) => {
        setUserAnswer(event.target.value);
    };

    const handleCheckAnswer = () => {
        const apiKey = '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';

        const modelAnswer = `Dear Ms. McKeen,

My name is Leo Smith and I am a first-year student in your Marketing class at Capilano College. I am writing to you because I have a problem with the project you set about social media. The due date was last week but I'm afraid I haven't finished it yet. As you know, I have been absent from class for almost ten days with COVID-19 symptoms and I have not been attending lectures. I only managed to get the reading list from a friend and go to the library to do research yesterday. You will see from your records that I have never missed a deadline for an assignment so far during my course.

However, I am requesting an extension of one week in this case because I am so behind with my work. I hope you will take my illness into consideration and grant me an extension.

Yours sincerely,
Leo Smith`;

        axios.post('https://api.openai.com/v1/engines/text-davinci-002/completions', {
            prompt: `You are an IELTS teacher checking a General IELTS Writing module task one. The User will write a letter. The question is in the following brackets: (You are taking a course at a local college. The deadline for your project was last week but you haven't finished it. Write a letter to your lecturer. In your letter introduce yourself, explain why you haven't handed in the project yet, request more time to do it.)You will judge user answer based on model answer and give feedback,  give remarks where user lacks, what things can be improve, also score user's answer(It should be lenient) with respect to model answer. You will check user answer with respect to model answer given :\n\nModel answer: ${modelAnswer}\n\nUser answer: ${userAnswer}. `,
            max_tokens: 500, 
        }, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer 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`,
            },
        })
        .then((response) => {
            const aiFeedback = response.data.choices[0].text;

            setFeedback(aiFeedback);
        })
        .catch((error) => {
            console.error(error);
        });
    };

    return (
        <div className="general_writing-task-container">
           
            <h2>IELTS General Writing Task 1</h2>
            <p>You are taking a course at a local college. The deadline for your project was last week but you haven't finished it.</p>
            <p>Write a letter to your lecturer. In your letter</p>
            <ol>
                <li>Introduce yourself</li>
                <li>Explain why you haven't handed in the project yet</li>
                <li>Request more time to do it.</li>
            </ol>
            <hr />
            <div className="letter-input">
                <label htmlFor="letterContent">Your Letter:</label>
                <textarea
                    id="letterContent"
                    name="letterContent"
                    rows="10"
                    cols="50"
                    placeholder="Write your letter here..."
                    value={userAnswer}
                    onChange={handleUserAnswerChange}
                ></textarea>
            </div>
            <button className="writing-check-button" onClick={handleCheckAnswer}>Check</button>


            {feedback && (
                <div className="feedback">
                    <h3>Feedback:</h3>
                    <p>{feedback}</p>
                </div>
            )}
        </div>
    );
};

export default GeneralWritingTask1;
