import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // Import BrowserRouter and Route
import Home from './components/Home';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import app from './config/firebase-config';
import Signin from './components/Signin';
import Signup from './components/Signup'; // Import the Signup component

function App() {
  const [auth, setAuth] = useState(false || window.localStorage.getItem("auth") === 'true');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState('');

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (userCred) => {
      if (userCred) {
        setAuth(true);
        window.localStorage.setItem("auth", "true");
        userCred.getIdToken().then((token) => {
          setToken(token);
        });
      } else {
        setUser(null);
      }
    });

    return () => unsubscribe();
  }, []);

  return (
    <Router>
      <div>
        <Switch>
          <Route exact path="/" render={() => (
            auth ? <Home token={token} /> : <Signin setAuth={setAuth} setToken={setToken} />
          )} />
          <Route path="/signup" component={Signup} /> {/* Route for the Signup component */}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
